import React, { Component } from 'react';
import { Badge,Media,Navbar,NavbarBrand,Button,label} from 'reactstrap';

class LeftComponent extends Component {
    constructor (props) {
        super(props)

        this.state = {
            selectrdProvider:null
        }

    }

    onProviderSelect(provider){
        this.setState({selectrdProvider:provider});

    }

    renderProvider(provider) {
        if (provider != null) {
            return(
                <div>
                    <Media >{provider.name}</Media>
                   <Media> {provider.description}</Media>
                </div>

            );
        }
         else {
             return(
                 <div></div>
             );
         }
           

    }


    render() {
        const list = this.props.network.map((provider) =>{
            return(
                <div key={provider.id} className="col-15 mt-3">
                     <Media onClick ={ () => this.onProviderSelect(provider) } >
                         <Media className="col-10">{provider.name}  </Media>
                         <Media className="col-10">  &#x20B9; {provider.price}</Media>
                     </Media>

                </div>
            );

        }); 



        return (
            <div>
                <div className="container">
                    <div className="row">
                        <Media list>
                            {list}
                        </Media>
                    </div>
                    <div className="col">
                        <Media>
                            {this.renderProvider(this.state.selectrdProvider)}
                        </Media>
                    </div>

                    <form>
                    <div className="container">
                    <h3> 
                        <Badge color="secondary">Sort By</Badge>

                        <Badge className="badge badge-secondary">
                            <div className="radio">
                            <label>
                                <input type="radio" value="Price" name="option" />
                                Price
                            </label>
                            <label>
                                <input type="radio" value="Rating" name="option"/>
                                Rating 
                            </label>
                            </div>
                        </Badge>
                    </h3>
                    </div>
                </form>


                </div>
                

                <table className="table">
                    <thead>
                        <tr>
                            <th>Home     <input type="text" placeholder="Search.."></input> </th>
                        </tr>
                    </thead>
                    <tfoot>
                        <tr>
                            <td>{list}</td>
                        </tr>
                    </tfoot>
                    <tfoot>
                        <tr>
                            <td>logo  Airtel............ Rs 200</td>
                        </tr>
                    </tfoot>
                </table>


                


            </div>
        );
    }
}

export default LeftComponent;
