import React, { Component } from 'react';
import { Badge,Media,Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Col,Row} from 'reactstrap';

class LeftComponent extends Component {
    constructor (props) {
        super(props)

        this.state = {
            selectrdProvider:null
        }

    }

    onProviderSelect(provider){
        this.setState({selectrdProvider:provider});

    }

    renderProvider(provider) {
        if (provider != null) {
            return(
                <div>
                    <Card>
                        <CardBody>
                            <CardTitle>{provider.name}</CardTitle>
                            <CardText> {provider.description}</CardText>
                        </CardBody>
                   </Card>
                </div>

            );
        }
         else {
             return(
                 <div></div>
             );
         }
           

    }


    render() {
        const list = this.props.network.map((provider) =>{
            return(
                <div key={provider.id} className="col-15 mt-3">
                     <Card onClick ={ () => this.onProviderSelect(provider) } >
                         <Media className="col-10">{provider.name}  </Media>
                         <Media className="col-10">  &#x20B9; {provider.price}</Media>
                     </Card>

                </div>
            );

        }); 



        return (
            <div>
                    <div className="col-6">
                        <Media list>
                            {list}
                        </Media>

                        <form>
                            <div className="container">
                            <h3> 
                            <Badge color="light">Sort By</Badge>
                            <Badge className="badge badge-secondary">
                                <div className="radio">
                                    <label><input type="radio" value="Price" name="option" />Price</label>
                                    <label><input type="radio" value="Rating" name="option"/>Rating</label>
                                </div>
                            </Badge>
                            </h3>
                            </div>
                        </form>

                    </div>
                   
                    <div className="col-12">
                        <Row>
                            <Col>
                                dshd
                            </Col>
                            <Col>
                                <div className="col-6">
                                    <Media>
                                        {this.renderProvider(this.state.selectrdProvider)}
                                    </Media>
                                 </div>
                            </Col>
                        </Row>
                        <div className="col-6">Right</div>
                        <div className="col-6">Left</div>
                    </div>
            </div>
        );
    }
}

export default LeftComponent;
